var query=getUrlParameter("q"),filter=query.toUpperCase(),list=document.getElementById("posts"),listItems=list.getElementsByTagName("div");document.getElementById("results-for").innerHTML=query;for(var i=0;i<listItems.length;i++)0>listItems[i].querySelector("h1").innerHTML.toUpperCase().indexOf(filter)&&(listItems[i].style.display="none");
function getUrlParameter(a){a=a.replace(/[\[]/,"\\[").replace(/[\]]/,"\\]");a=(new RegExp("[\\?&]"+a+"=([^&#]*)")).exec(location.search);return null===a?"":decodeURIComponent(a[1].replace(/\+/g," "))};
